#include "modules/video_coding/codecs/vp8/vp8_scalability.h"

namespace webrtc {

bool VP8SupportsScalabilityMode(ScalabilityMode scalability_mode) {
  for (const auto& entry : kVP8SupportedScalabilityModes) {
    if (entry == scalability_mode) {
      return true;
    }
  }
  return false;
}

}  // namespace webrtc
